import React, { Component } from 'react';

export class DeleteEvent extends Component {
    id = React.createRef();
    state = {msg:''};
    DeleteEmployeeById() {
        var id=this.id.current.value;
        var url=`http://localhost:3001/employees/${id}`;
        fetch(url,{
            method:"DELETE",
        }).then(()=> this.setState({msg:"Row Delete Successfully"}))
        .catch( error => this.setState({msg:error}));
    }
    render() {
        return (
            <div>
                  <h1> Employee Delete</h1>
                Enter Id : <input type="number" ref={this.id}></input>
                <button onClick={()=>this.DeleteEmployeeById()}>Detail</button><br/>
                {this.state.msg}
            </div>
        )
    }
}
export default DeleteEvent;
