import React, { Component } from 'react'
import { store, login, logout } from "./Store"
class Login extends Component {
    state = {
        msg: '',
        user: [
            { 'uname': 'gopi', 'password': 'gopi' },
            { 'uname': 'aman', 'password': 'aman' },
            { 'uname': 'ram', 'password': 'ram' },
            { 'uname': 'rohan', 'password': 'rohan' },
        ],
        status: "logout"
    }
    // validation(){
    //     if(this.state.user.find(x=>x.uname==uname && x.password==password)){
    //         this.setState({msg:'valid'});
    //     }
    // }
    render() {
        return (
            <div>
                <h1>Log in</h1>
                <form class="row g-3 container">
                    <div class="mb-3">
                        <label for="exampleFormControlInput1" class="form-label">Email address</label>
                        <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="User name" />
                    </div>
                    <div class="col-auto">
                        <label for="inputPassword2" class="visually-hidden">Password</label>
                        <input type="password" class="form-control" id="inputPassword2" placeholder="Password" />
                    </div>
                    <div class="col-auto">
                        <button type="submit" class="btn btn-primary mb-3">Confirm identity</button>
                    </div>
                </form>
            </div>
        )
    }
}
export default Login;
