import React, { Component } from 'react'
import Error1 from './Error';
import DashboardComp from '../DashBoard/DashboardComp';
import ReactDOM from 'react-dom';

import { store, login, logout } from "./Store"
import reactDom from 'react-dom';
class Login extends Component {
    uname = React.createRef();
    password = React.createRef();
    state = {
        msg: '',
        user: [
            { 'uname': 'gopi', 'password': 'gopi' },
            { 'uname': 'aman', 'password': 'aman' },
            { 'uname': 'ram', 'password': 'ram' },
            { 'uname': 'rohan', 'password': 'rohan' },
        ],
        status: "invalid"
    }
    validation() {
        var uname = this.uname.current.value;
        var password = this.password.current.value;
        if (this.state.user.find(x => x.uname == uname && x.password == password)) {
            store.dispatch(login(uname));
            this.setState({ status: 'valid', class1: 'successClass', msg: "Successfully Login" });
        }
        else {
            store.dispatch(logout());
            this.setState({ status: 'invalid', class1: 'failureClass', msg: "Invalid User Name and Password" })
        }
        var element='';
        // if(this.state.status=='valid'){
        //     element=<DashboardComp />
        // }
        // else{
        //     Element=<Error1 />
        // }
        // ReactDOM.render(element,document.getElementById('div1'));
    }
    render() {
        return (
            <div className={this.state.class1}>
                <h1>Log in</h1>
                <input type="text" placeholder="User name" ref={this.uname} />
                <input type="password" placeholder="Password" ref={this.password} />
                <button className="btn btn-primary mb-3" onClick={() => this.validation()}>Confirm identity</button>
                {this.state.status=='valid'?  <DashboardComp />:<Error1 />}
                {/* <div id='div1'></div> */}
               
            </div>
        )
    }
}
export default Login;
