import React, { Component } from 'react'

class EventDetail extends Component {
    id = React.createRef();
    state = {event:{}};
    getEmployeeById() {
        var id=this.id.current.value;
        var url=`http://localhost:3001/event/${id}`;
        fetch(url)
            .then(response => response.json())
            .then(response => this.setState({event:response}))
            .catch(error => this.setState({error:error}));
    }
    render() {
        return (
            <div className='container'>
                <h1> Employee Detail </h1>
                <div className='row'>
                <p >Enter Id : </p>
                <div class="col-sm-3 mx-3 my-3">
                <input type="number" ref={this.id}></input>
                </div>
                <button onClick={()=>this.getEmployeeById()}>Detail</button>
                </div>
                eventName: {this.state.event.eventName}<br/>
                
                Date: {this.state.event.Date}<br/>
              
            </div>
        )
    }
}

export default EventDetail;
