import React, { Component } from 'react';

class DeleteEvent extends Component {
    id = React.createRef();
    state = {msg:''};
    DeleteEmployeeById() {
        var id=this.id.current.value;
        var url=`http://localhost:3001/employees/${id}`;
        fetch(url,{
            method:"DELETE",
        }).then(()=> this.setState({msg:"Row Delete Successfully"}))
        .catch( error => this.setState({msg:error}));
    }
    render() {
        return (
            <div>
                <h1>Detele Event</h1>
                <input className="form-control" type="text" placeholder="Event Name" ref={this.id}/>
                <button className="btn btn-danger" onClick={()=>this.Create()}>Add</button>
                {this.state.msg}
            </div>
        )
    }
}
export default DeleteEvent;
