import React from 'react';
class EditEvent extends React.Component{
    id= React.createRef();
    eventName= React.createRef();
    Date=React.createRef();
    
    state={mgs:''};
    EditEvent(){
        var id=Number(this.id.current.value);
        var url=`http://localhost:3001/event/${id}`;
        var e={
            id:Number(this.id.current.value),
            eventName:this.eventName.current.value,
            Date:this.Date.current.value,
        };
        fetch(url,{
            method:"PUT",
            body: JSON.stringify(e),
            headers: {"content-type":"application/json"}
        }).then(()=> this.setState({msg:"Row Edit Successfully"}))
        .catch( error => this.setState({msg:error}));
    }
        render(){
            return <div>
                <h1>Edit Event Detail</h1>
                <h1>{this.state.msg}</h1> 
                Id : <input type='number' ref={this.id} /><br></br>
                Event Name : <input type='text' ref={this.eventName} /><br></br>
                Date : <input type='text' ref={this.Date} /><br></br>
                <button onClick={()=>this.EditEvent()}>Edit</button>
               
            </div>
        }
}
export default EditEvent;
