import React from 'react';
class EditEvent extends React.Component{
    id= React.createRef();
    eventName= React.createRef();
    Date=React.createRef();
    
    state={mgs:''};
    EditEvent(){
        var id=Number(this.id.current.value);
        var url=`http://localhost:3001/event/${id}`;
        var e={
            id:Number(this.id.current.value),
            eventName:this.eventName.current.value,
            Date:this.Date.current.value,
        };
        fetch(url,{
            method:"PUT",
            body: JSON.stringify(e),
            headers: {"content-type":"application/json"}
        }).then(()=> this.setState({msg:"Row Edit Successfully"}))
        .catch( error => this.setState({msg:error}));
    }
        render(){
            return <div>
                
                <h1>Edit Event Detail</h1>
                {/* Id : <input type='number' className="form-control " ref={this.id} /><br> */}
                <div class="mb-3 row">
                    <label for="inputPassword2" >passs</label>
                    <div class="col-sm-4">
                    <input type="number" ref={this.id} class="form-control" id="inputPassword2" placeholder="Enter ID"/>
                    </div>
                 </div>
                 <div class="col-auto">
                    <label for="inputPassword3" >passs</label>
                    <input type="text" ref={this.eventName} class="form-control" id="inputPassword3" placeholder="Enter Name"/>
                 </div>
                 <div class="col-auto">
                    <label for="inputPassword3" >Date</label>
                    <input type="text" ref={this.Date} class="form-control" id="inputPassword3" placeholder="Enter Name"/>
                 </div>
              
                <div class="col-auto">
                <button  class="btn btn-primary mb-3" onClick={()=>this.EditEvent()}>Edit</button></div>
                {this.state.msg}
                </div>
               
        }
}
export default EditEvent;
